mapper.update:
update member
set m_name        = #{m_name},
    m_phonenumber = #{m_phonenumber},
    m_address     = #{m_address},
    m_email       = #{m_email}
where m_id = #{m_id}

mapper.select:
select count(m_id)
from member
where m_id = #{m_id}

select m_email
from member
where m_id = #{m_id}

mapper.update:
update member
set m_password = #{m_password}
where m_id = #{m_id}

mapper.select:
select count(m_phonenumber)
from member
where m_phonenumber = #{m_phonenumber}

select m_email
from member
where m_phonenumber = #{m_phonenumber}

select m_name
from member
where m_phonenumber = #{m_phonenumber}

select m_id
from member
where m_phonenumber = #{m_phonenumber}

mapper.update:
update member
set m_password = #{m_password}
where m_id = #{m_id}
