mapper.select:
select count(*)
from movieLike
where mo_number = #{mo_number}
  and m_id = #{m_id}

mapper.update:
update movie
set mo_recommendation = mo_recommendation + 1
where mo_number = #{mo_number}

mapper.delete:
DELETE
FROM movieLike
WHERE mo_number = #{mo_number}
  and m_id = #{m_id}

mapper.update:
update movie
set mo_recommendation = mo_recommendation - 1
where mo_number = #{mo_number}
