mapper.select:
SELECT *
FROM (
        SELECT rownum rn ,  a.*
		FROM  		(SELECT * 
                     FROM   		BBS 
                     WHERE  		bbs_category=#{bbs_category} 
                     ORDER BY    bbs_no DESC
                     ) a
     )
WHERE rn between #{start} AND  #{end}

mapper.update:
   		UPDATE bbs
		  SET bbs_title = #{bbs_title}, bbs_content = #{bbs_content}
		  where bbs_no=${bbs_no}  and  bbs_category=${bbs_category}
