mapper.select:

SELECT Count(*)
FROM bbs
WHERE bbs_category = #{bbs_category}

SELECT *
FROM (
         SELECT rownum rn, a.*
         FROM (SELECT *
               FROM BBS
               WHERE bbs_category = #{bbs_category}
               ORDER BY bbs_no DESC
              ) a
     )
WHERE rn between #{start} AND #{end}

select *
From bbs
Where bbs_no = #{bbs_no}
  and bbs_category = #{bbs_category}

mapper.update:
UPDATE bbs
SET bbs_hit=bbs_hit + 1
WHERE bbs_category = #{bbs_category}
  and bbs_no = #{bbs_no}

mapper.delete:
delete
from bbs
where bbs_no = #{bbs_no}
  and bbs_category = #{bbs_category}

mapper.update:
UPDATE bbs
SET bbs_title   = #{bbs_title},
    bbs_content = #{bbs_content}
where bbs_no = ${bbs_no}
  and bbs_category = ${bbs_category}
